        public static void ARCOperations(string pathToFiles, string file, ProcessStartInfo startInfo, Process process)
            {
            startInfo.WindowStyle = System.Diagnostics.ProcessWindowStyle.Hidden;
            startInfo.WorkingDirectory = pathToFiles;
            startInfo.UseShellExecute = true;
            startInfo.FileName = "ARCtool.exe";
            startInfo.Arguments = file + " -alwayscomp -xfs -re5 -texRE5 -v 7 -pc";
            process.StartInfo = startInfo;
            Process.Start(startInfo);
            Thread.Sleep(10);
            }

        public static string organizeXML(string fileContent, int startIndex, string fileName, Random lifeRandom, Enemy currentEnemy, Dictionary<string, decimal> lifeMult)
            {

            string returnValue = "";

            //Integers for saving the position values of the current enemy, to be added to the replaced enemy's info.
            int indexOfPosition = fileContent.IndexOf("<vector3 name=\"mPosition\"", startIndex);
            int indexOfEndPosition = fileContent.IndexOf("/>", indexOfPosition) + 2;

            //Generates a random health value for the enemy between the enemy specific minimum and maximum.
            int randomHealthValue = lifeRandom.Next(currentEnemy.minLife, currentEnemy.maxLife);

            //Stores only the current level's file name ("s104" for example).
            string levelName = fileName.Substring(0, 4);

            //Multiplies the randomly generated health value by the current level's
            int multipliedHealth = Convert.ToInt32(lifeMult[levelName] * randomHealthValue);

            //Adds selected enemy info and old enemy position values to the new file string.
            returnValue += "\r\n\t\t\t\t";
            returnValue += fileContent.Substring(indexOfPosition, indexOfEndPosition - indexOfPosition);
            returnValue += "\r\n\t\t\t\t<u32 name=\"mLife\" value=\"" + multipliedHealth + "\"/>";
            returnValue += "\r\n\t\t\t</classref>\r\n\t\t</classref>";

            return returnValue;
            }
