                spoilerLog += "All enemies\r\n";
                }
            else
                {
                spoilerLog += "Only safe enemies\r\n";
                }

            //Stores pairs of level names and their respective health multiplier based on progress through game.
            Dictionary<string, decimal> lifeMult = new Dictionary<string, decimal>
                {
                    { "s100", 1.0M },
                    { "s115", 1.0M },
                    { "s116", 1.0M },
                    { "s114", 1.0M },
