                    { "s200", 1.4M },
                    { "s202", 1.4M },
                    { "s201", 1.4M },
                    { "s207", 1.4M },
                    { "s203", 1.4M },
                    { "s209", 1.4M },
                    { "s204", 1.4M },
                    { "s205", 1.4M },
                    { "s313", 1.6M },
                    { "s300", 1.6M },
                    { "s301", 1.6M },
